import React from 'react';
import './EventsPage.css'; 

function EventsPage() {
  const events = [
    { name: 'Music Concert', date: '2025-03-01', location: 'New York', ticketPrice: '$50' },
    { name: 'Tech Conference', date: '2025-03-15', location: 'San Francisco', ticketPrice: '$100' },
    { name: 'Art Exhibition', date: '2025-04-01', location: 'Los Angeles', ticketPrice: '$30' }
  ];

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4">Upcoming Events</h2>
      <div className="row">
        {events.map((event, index) => (
          <div key={index} className="col-md-4 mb-4">
            <div className="card shadow event-card">
              <img src="https://via.placeholder.com/400x200" className="card-img-top" alt="Event" />
              <div className="card-body">
                <h5 className="card-title">{event.name}</h5>
                <p className="card-text"><strong>Date:</strong> {event.date}</p>
                <p className="card-text"><strong>Location:</strong> {event.location}</p>
                <p className="card-text"><strong>Price:</strong> {event.ticketPrice}</p>
                <button className="btn btn-primary w-100">Book Now</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default EventsPage;
